mapper.select:
select event.event_id,event.event_name,employee_master.emp_name from event inner join employee_master on event.host_num = employee_master.emp_num where event.decision_day is null
and event.host_num = #{hostNum}
and event.category_id = #{categoryNum}
order by event.event_id desc

select event.event_id,event.event_name,employee_master.emp_name,event.decision_day from event inner join employee_master on event.host_num = employee_master.emp_num where event.decision_day is not null and event.decision_day >= CURRENT_DATE()
and event.host_num = #{hostNum}
and event.category_id = #{categoryNum}
and event.decision_day >= #{startDay}
and event.decision_day <= #{endDay}
order by event.decision_day ${sort}
order by event.decision_day

select event.event_id,event.event_name,employee_master.emp_name,event.decision_day from event inner join employee_master on event.host_num = employee_master.emp_num where event.decision_day is not null and event.decision_day < CURRENT_DATE()
and event.host_num = #{hostNum}
and event.category_id = #{categoryNum}
and event.decision_day >= #{startDay}
and event.decision_day <= #{endDay}
order by event.decision_day ${sort}
order by event.decision_day desc

mapper.update:
update
	event
set
	event.decision_day = #{candidateDay}
where
	event.event_id = #{eventId}

update
	event
set
	event.event_name = #{eventName}
	,event.category_id =  #{categoryId}
	,event.place =  #{place}
	,event.host_comment =  #{hostComment}
where
	event.event_id = #{eventId}
